mapper.select:
select *
from customer where storeid = #{storeid}

    and customer_name = #{customer_name}

mapper.update:
update customer
set deal = #{deal}
where id = #{cid};

mapper.select:
select *
from customer where 1 = 1

    and storeid = #{storeid}


    and customer_name = #{customer_name};

select count(1)
from customer where 1 = 1

    and storeid = #{storeid}

select distinct drainage_channel
from customer where 1 = 1

    and storeid = #{storeid}

select count(1)
from customer where drainage_channel = #{channelname}

    and storeid = #{storeid}

select count(1)
from customer where drainage_channel = #{channelname}
                and deal = 1

    and storeid = #{storeid}
