mapper.select:
select *
from customer where storeid = #{storeid}

    and customer_name = #{customer_name}

and state = 1;

mapper.update:
update customer
set deal = #{deal}
where id = #{cid}
  and state = 1;

mapper.select:
select * from customer where 1 = 1
and storeid = #{storeid}
and customer_name like concat('%', #{customer_name}, '%') or phone like concat('%', #{customer_name}, '%')
and state = 1 limit #{start},#{size}

select count(1)
from customer where 1 = 1

    and storeid = #{storeid}

and state = 1;

select distinct drainage_channel
from customer where 1 = 1

    and storeid = #{storeid}

and state = 1;

select count(1)
from customer where drainage_channel = #{channelname}

    and storeid = #{storeid}

and state = 1;

select count(1)
from customer where drainage_channel = #{channelname}
                and deal = 1

    and storeid = #{storeid}

and state = 1;

mapper.update:
update customer set

    customer_name =#{customer.customer_name},


    birthday = #{customer.birthday},


    drainage_channel = #{customer.drainage_channel},


    phone = #{customer.phone},


    sex = #{customer.sex},


    storeid = #{customer.storeid}

where id = #{id}
  and state = 1;

update customer set

    state = #{state}

where id = #{id}

mapper.select:
select customer_name
from customer


        and id = #{cid}

    and state = 1;

select count(1)
from customer where 1 = 1

    and storeid = #{storeid}


    and customer_name like concat('%', #{customer_name}, '%')

and state = 1;
