mapper.select:
select count(1)
from store


        and store_name = #{store_name}

    and state = 1;

select count(1)
from store


        and store_name = #{address}

    and state = 1;

select distinct city
from store
where state = 1;

select *
from store


        and city = #{city}


        and store_name like concat('%', #{store_name}, '%')

    and state = 1 limit #{start},#{size};

select count(1)
from store


        and id = #{id}

    and state = 1;

select store_name
from store


        and id = #{storeid}

    and state = 1;

mapper.update:
update store set

    city = #{store.city},


    store_name = #{store.store_name},


    manager_name = #{store.manager_name},


    phone = #{store.phone}

where id = #{id}
  and state = 1;

update store set

    state = #{state}

where id = #{id}
  and state = 1;
